function $$(name) {
    var el = app.w(name).base();
    if (el.selector == "td")
        el = el.prevObject;
    return el;
}
function showWebsite(url) {
    if (app.isNativeApp()) {
        if (navigator.app)
            navigator.app.loadUrl(url,  { openExternal:true })
        else
            window.open(url, "_system");
    } else
        window.open(url);
}
function checkConnection() {
    if (!app.isNativeAppOnline()) {
        setTimeout(checkConnection, 5000);
    } else {
        location.reload();
    }
}
function saveScrollPage() {
    showBusy();
    var Y = $("body").scrollTop();
    if (Y === 0)
        Y = $("html").scrollTop();
    if (Y === 0) {
        if (app.currentPage().name() == "pageLijst")
            Y = -$$("pageLijst").scrollTop();
        if (app.currentPage().name() == "pageInfo")
            Y = -$$("pageInfo").scrollTop();
    }
    if (app.currentPage().name() == "pageLijst") {
        lijstY = Y;
    }
    if (app.currentPage().name() == "pageInfo") {
        infoY = Y;
    }
}
function handler_Plukwaar__productie__cloned__onAppStarted() {
    window.showSite = function(url) {
        showWebsite(url);
    };
    // Sort bomen on vrucht and naam
    BomenS.sort(function(a, b) {
        if (a.vrucht_soort !== b.vrucht_soort)
            return (a.vrucht_soort < b.vrucht_soort ? -1 : 1);
        if (a.boom_nl !== b.boom_nl)
            return (a.boom_nl < b.boom_nl ? -1 : 1);
        return (a.boom_latijn < b.boom_latijn ? -1 : 1);
    });
    htmlLijstSet = false;

    if (!app.isNativeAppOnline()) {
        app.showMessage("Geen internet", "Plukwaar kan geen netwerkverbinding vinden. Toegang tot internet is nodig om deze app te kunnen gebruiken.");
        checkConnection();
    }

    document.addEventListener("touchstart", function() { 
        if (app.currentPage().name() == "pageLijst") {
            $$("lijst").focus();
        }
        if (app.currentPage().name() == "pageLijst") {
            $$("label").focus();
        }   
    });

}
