function setNewMarkersDo() {
    var X; var Y; var block; var P; var rijp; var fileName; var file; var img; 
    var curBlokStart = [];
    var curBlokEnd = [];
    var curMarkerBlocks = [];
    var addMarkerBlocks = [];
    var delMarkerBlocks = [];

    // Init
    showBusy();
    var dezeMaand = new Date().getMonth() + 1;

    // Get new map bounds
    var NE = app.w('gmMap').googleMap().getBounds().getNorthEast();
    var SW = app.w('gmMap').googleMap().getBounds().getSouthWest();

    // Determine blocks of this bound
    curBlokStart[0] = getBlokX(SW.lng());
    curBlokStart[1] = getBlokY(NE.lat());
    curBlokEnd[0] = getBlokX(NE.lng());
    curBlokEnd[1] = getBlokY(SW.lat());

    // Blocks have changed?
    if ((curBlokStart[0] !== prevBlokStart[0]) || (curBlokStart[1] !== prevBlokStart[1]) || (curBlokEnd[0] !== prevBlokEnd[0]) || (curBlokEnd[1] !== prevBlokEnd[1])) {
        // Blocks have changed, now determine difference between previous and current blocks
        for (X=curBlokStart[0]; X<=curBlokEnd[0]; X++) {
            for (Y=curBlokStart[1]; Y>=curBlokEnd[1]; Y--) {
                block = X*20 + Y;
                curMarkerBlocks.push(block);
                if (prevMarkerBlocks.indexOf(block) == -1)
                    addMarkerBlocks.push(block);
            }
        }

        // Determine deleted blocks
        for (X=0; X<prevMarkerBlocks.length; X++) {
            if (curMarkerBlocks.indexOf(prevMarkerBlocks[X]) == -1)
                delMarkerBlocks.push(prevMarkerBlocks[X]);
        }

        // Add new markers
        var zoomNowLocal = app.w('gmMap').googleMap().getZoom();
        sizeDir = "";
        if (zoomNowLocal <= 15)
            sizeDir = "klein";
        if ((zoomNowLocal == 16) || (zoomNowLocal == 17))
            sizeDir = "midden";
        if (zoomNowLocal == 18)
            sizeDir = "midden2";
        if (zoomNowLocal >= 19)
            sizeDir = "groot";

        // On small (slow) devices always the same size
        if (smallScreen)
            sizeDir = "midden";

        switch(sizeDir) {
            case "klein":
              size = new google.maps.Size(48, 40);
              anchor = new google.maps.Point(12, 38);
              break;

            case "midden":
              size = new google.maps.Size(72, 60);
              anchor = new google.maps.Point(19, 58);
              break;

            case "midden2":
              size = new google.maps.Size(96, 80);
              anchor = new google.maps.Point(25, 78);
              break;

            case "groot":
              size = new google.maps.Size(144, 120);
              anchor = new google.maps.Point(38, 118);
              break;
        }

        for (X=0; X<addMarkerBlocks.length; X++) {
            block = addMarkerBlocks[X];
            if (Amersfoort_blok_index[block]) {

                Y = Amersfoort_blok_index[block];
                while (Amersfoort.features[Y].geometry.blok == block) {

                    P = Amersfoort.features[Y];
                    rijp = "or";
                    if (P.properties.rijp1 == dezeMaand || P.properties.rijp2 == dezeMaand)
                      rijp = "r";
                    fileName = "marker_" + rijp + "_" + P.properties.vrucht + ".png";


                    file = "http://www.brouwerinbeeld.nl/app/afbeeldingen/markers/" + sizeDir + "/" + fileName;
                    img = {
                      url: file,
                      size: size,
                      anchor: anchor,
                      origin: new google.maps.Point(0, 0),
                      fileName: fileName
                    };

                    markers.push(new google.maps.Marker({ 
                      position: new google.maps.LatLng(P.geometry.coordinates[1], P.geometry.coordinates[0]),
                      icon: img,
                      title: P.properties.vrucht,
                      boomID: P.properties.boomID,
                      block: block
                    }));

                    i = markers.length - 1;
                    markers[i].setMap(app.w('gmMap').googleMap());
                    markerListener[i] = google.maps.event.addListener(markers[i], "click", function(event) { markerClicked(this, event) });

                    Y++;
                    if (Y >= Amersfoort.features.length)
                        break;
                }
            }

        }

        // Delete unused markers
        for (i=markers.length-1; i>=0; i--) {
            if (delMarkerBlocks.indexOf(markers[i].block) !== -1) {
                markers[i].setMap(null);
                google.maps.event.removeListener(markerListener[i]);
                markers.splice(i, 1);
                markerListener.splice(i, 1);
            }

        }

        // Finish
        prevMarkerBlocks = curMarkerBlocks;
        prevBlokStart[0] = curBlokStart[0];
        prevBlokStart[1] = curBlokStart[1];
        prevBlokEnd[0] = curBlokEnd[0];
        prevBlokEnd[1] = curBlokEnd[1];
    }

