function markerClicked(marker, event) {
  // Streetview has priority over click on marker
  if (svPointing) {
      setStreetView(marker.position);
      return;
  }

  showBusy();
  var boom = Bomen[marker.boomID];
  var html = 
      "<div class='boom'>" +
      "<h3>" + boom.boom_nl + "</h3>" +
      "<table>" +
      "<tr><td class='label'>latijnse naam</td><td>" + boom.boom_latijn + "</td></tr>" +
      "<tr><td class='label'>vrucht</td><td>" + boom.vrucht + "</td></tr>" +
      "<tr><td class='label'>rijp in</td><td><table class='kalender'>";
  for (var i=1; i<=12; i++) {
    if (i==1)
      html += "<tr>";
    if (i==7)
      html += "</tr><tr>";
    if ((i==boom.rijp1) || (i==boom.rijp2))
      html += "<td class='rijp'>"
    else
      html += "<td>";
    html += maanden[i - 1] + "</td>";
  }
  html += 
      "</tr></table></td></tr>" +
      "<tr><td class='label'>links</td>" +
      "<td><p><a href='#' onclick='showSite(\"" + boom.link1 + "\")'>" + boom.omschrijving_link1 + "</a></p>";
  if (boom.link2 !== "")
    html += "<p><a href='#' onclick='showSite(\"" + boom.link2 + "\")'>" + boom.omschrijving_link2 + "</a></p>";
  html += "</td></tr></table>";

  // Add images
  W = 126;
  H = Math.round((W / boom.afb1W) * boom.afb1H);
  html +=
    "<table class='afb'><tr>" +
    "<td><img id='marker_afb1' src='http://www.brouwerinbeeld.nl/app/" + boom.afb1 + "' width='" + W + "' height='" + H + "'></td>";
  if (boom.afb2) {
    H = Math.round((W / boom.afb2W) * boom.afb2H);
    html +=
      "<td><img id='marker_afb2' src='http://www.brouwerinbeeld.nl/app/" + boom.afb2 + "' width='" + W + "' height='" + H + "'></td>";
  } else {
    html += "<td></td>";
  }
  html += "</tr></table></div>";

  // Close old window
  if (infoW)
    infoW.close();

  // Open new window
  infoW = new google.maps.InfoWindow({ 
    content: html,
    pixelOffset: new google.maps.Size(-12, 12),
    maxWidth: 270
  });

  hideBusy();
  if (svActive && (!retina)) {
    infoW.open(pano, marker);
  } else {
    infoW.open(app.w('gmMap').googleMap(), marker);
  }

  google.maps.event.addListener(infoW, "closeclick", closeMarker);
  setTimeout(function() {
      $("#marker_afb1").click(function() { showEnlargedImage(1, marker.boomID) });
      if (boom.afb2) {
          $("#marker_afb2").click(function() { showEnlargedImage(2, marker.boomID) });
      }
  }, 1000);
  hideBottomBar();
  setTimeout(infoNotUnderTopBar, 500);
}

