function setIconSize() {
  var i; var fileName; var file; var size; var anchor;

  if (smallScreen) {
      zoomPrev = zoomNow;
      return;
  }
  showBusy();

  sizeDir = "";
  if ((zoomNow <= 15) && (zoomPrev > 15))
    sizeDir = "klein";
  if (((zoomNow == 16) || (zoomNow == 17)) && ((zoomPrev <= 15) || (zoomPrev >= 18)))
    sizeDir = "midden";
  if ((zoomNow == 18) && (zoomPrev !== 18))
    sizeDir = "midden2";
  if ((zoomNow >= 19) && (zoomPrev < 19))
    sizeDir = "groot";

  if (sizeDir !== "") {

    switch(sizeDir) {
        case "klein":
          size = new google.maps.Size(48, 40);
          anchor = new google.maps.Point(12, 38);
          break;

        case "midden":
          size = new google.maps.Size(72, 60);
          anchor = new google.maps.Point(19, 58);
          break;

        case "midden2":
          size = new google.maps.Size(96, 80);
          anchor = new google.maps.Point(25, 78);
          break;

        case "groot":
          size = new google.maps.Size(144, 120);
          anchor = new google.maps.Point(38, 118);
          break;
    }

    for (i = 0; i < markers.length; i++) {
      fileName = markers[i].getIcon().fileName;
      file = "http://www.brouwerinbeeld.nl/app/afbeeldingen/markers/" + sizeDir + "/" + fileName;
      img = {
        url: file,
        size: size,
        origin: new google.maps.Point(0, 0),
        anchor: anchor,
        fileName: fileName
      };
      markers[i].setIcon(img);
    }
  }
  zoomPrev = zoomNow;
  hideBusy();    
}
