function showEnlargedImage(nr, id) {
    if ($("#imgLarge").length === 0)
        $$('boxImage').append("<img id='imgLarge'>");

    var sW = window.innerWidth;
    var sH = window.innerHeight;
    var boom = Bomen[id];
    var iW = (nr == 1 ? boom.afb1W : boom.afb2W);
    var iH = (nr == 1 ? boom.afb1H : boom.afb2H);
    var fX = (iW <= sW ? 1 : iW / (sW - 16));
    var fY = (iH <= sH ? 1 : iH / (sH - 16));
    var f = (fX > fY ? fX : fY);
    iW = iW / f;
    iH = iH / f;
    var iX = (sW - iW) / 2;
    var iY = (sH - iH) / 2;

    //debugger;
    $$('boxImage').css({top: 0, left: 0, width: sW, height: sH, zIndex: 10000, opacity: 1, display: "block"});
    $$('boxGreyed').css({top: 0, left: 0, width: sW, height: sH});
    $("#imgLarge").css({top: iY, left: iX, width: iW, height: iH, position: "absolute", border: "none", zIndex: 1000});
    $("#imgLarge").attr('src', 'http://www.brouwerinbeeld.nl/app/' + (nr == 1 ? boom.afb1 : boom.afb2));

    $("#imgLarge").off();
    $("#imgLarge").click(closeEnlargedImage);
