function showLijst() {
    var html; var col; var W; var H; var i; var boom; var cellW; var i2; var imgW;

    W = window.innerWidth;
    col = (W >= 1500 ? 3 : (W >=1000 ? 2: 1));
    cellW = (col == 1 ? "100%" : (col == 2 ? "50%" : "30%"));
    imgW = (W < 380 ? 80 : 180);

    if (htmlLijstSet && (col == inCol))
        return;

    html = "<h3 class='lijst'>Bomen met eetbare vruchten</h3>"
    $$('lijst').html(html);

    html = "<table class='bomenlijst'><tbody>";
    var ii = 0;
    for (i = 0; i < BomenS.length; i++) {
        boom = BomenS[i];

        if (boom.boom_nl) {
            if (ii % col === 0) {
                if (ii !== 0)
                html += "</tr>";
                html += "<tr class='color'>";
            }
            html += "<td width='" + cellW + "'><table class='lijst-entry'><tbody>";

            W = imgW;
            H = Math.round((W / boom.afb1W) * boom.afb1H);
            html += "<tr><td><img class='lijst' height='" + H + "' width='" + W + "' src='http://www.brouwerinbeeld.nl/app/" + boom.afb1 + "'></td>";
            html += "<td><h4>" + boom.boom_nl + "</h4>";
            html += "<p class='label'>latijnse naam</p>";
            html += "<p>" + boom.boom_latijn + "</p>";
            html += "<p class='label'>vrucht</p>";
            html += "<p>" + boom.vrucht + "</p>";

            html += "<p class='label'>rijp in</p>";
            html += "<p>" + maandenVol[boom.rijp1 - 1];
            if (boom.rijp2 !== boom.rijp1)
                html += ", " + maandenVol[boom.rijp2 - 1];
            html += "</p>";

            html += "<p class='label'>links</p>";
            html += "<p><a href='#' onclick='showSite(\"" + boom.link1 + "\")'>" + boom.omschrijving_link1 + "</a></p>";
            if (boom.link2 !== "")
                html += "<p><a href='#' onclick='showSite(\"" + boom.link2 + "\")'>" + boom.omschrijving_link2 + "</a></p>";

            html += "</td></tr></tbody></table></td>";
            ii++;
        }
    }
    html += "</tr></tbody></table>";

    $$('lijst').append(html);
    inCol = col;
    htmlLijstSet = true;
}
